import React from 'react';
import { connect } from 'react-redux';
import { checkLoggedIn } from '../actions/auth'

class Welcome extends React.Component {

   render() {
      console.log(this.props.loggedIn)
      return (
         <div>
            {this.props.loggedIn ? <p >Welcome, {this.props.user.username}</p> : <p style={"font-size: 10px"}>Welcome! Please create an account to begin your adventure.</p>}
         </div>
      )
   }
}

const mapStateToProps = state => {
   return {
      loggedIn: state.auth.loggedIn,
      user: state.auth.currentUser
   }
}

export default connect(mapStateToProps, { checkLoggedIn })(Welcome)
