import React, { Component } from 'react'
import { connect } from 'react-redux';
import { submitEntryField, updateEntryField } from '../actions/entryField'
import { executeCommand } from '../actions/commands'

class EntryField extends Component {

   state = {
      command: ''
   }

   handleChange = (event) => {
      const command = event.target.value
      this.setState({
         command: command
      })


      // const currentCommand = Object.assign({}, this.props.command, {
      //    command: command.toLowerCase()
      // })

      // this.props.updateEntryField(currentCommand)

   //    console.log(currentCommand)
   }

   handleSubmit = (event) => {
      event.preventDefault()
      this.setState({
         command: ''
      })
      console.log(this.state)
   }

   render() {
      return (
         <form onSubmit={this.handleSubmit} className="entry-form">
            <input type="text" onChange={this.handleChange} value={this.state.command} className="entry text" />
            <input type="submit" value="Develop the plot..." className="entry submit" />
         </form>
      )
   }
}

const mapStateToProps = state => {
   return {
     command: state.command
   }
}

// const mapDispatchToProps = dispatch => {
//    return {
//       commandUpdate: () => dispatch(updateEntryField),
//       commandSubmit: () => dispatch(submitEntryField)
//    }
// }

export default connect(mapStateToProps, { updateEntryField, submitEntryField, executeCommand })(EntryField);
