const defaultState = {
   command: ''
}

const commandReducer = (state=defaultState, action) => {
   switch (action.type) {
      case 'UPDATED_COMMAND':
         console.log({...state, command: action.command})
         return {...state, command: action.command}
      case 'RESET_COMMAND':
         return defaultState
      default:
         return state
   }
}

export default commandReducer
