import React from 'react';
import { Switch, Route, Link } from 'react-router-dom'
import { connect } from 'react-redux';

import logo from '../logo.svg';
import Inventory from '../containers/Inventory'
import SignUp from '../components/SignUp'
import SignIn from '../containers/SignIn'
import Welcome from '../components/Welcome'
import { logout } from '../actions/auth'

class Sidebar extends React.Component {
   render() {
      return (
         <div className="sidebar">
            <img src={logo} className="App-logo" alt="logo" />
            <Inventory />
            <br />
            {user.username ? <p>Welcome, {user.username}!</p> : <Link to="/signup" className="link">New Adventure</Link>}
            <br /><br />
            {user.username ? <Link to="" onClick={() => this.props.logout()}>Log out</Link> : <Link to="/signin" className="link">Continue Adventure</Link>}
            <Switch>
               <Route exact path="/" component={Welcome} />
               <Route exact path="/signin" component={SignIn} />
               <Route exact path="/signup" component={SignUp} />
            </Switch>
         </div>
      )
   }
}

const mapStateToProps = (state) => {
   return {
      user: state.auth.currentUser,
      logout: state.auth.logout
   }
}

export default connect(mapStateToProps, { logout })(SideBar)
