const defaultState = {
   command: ''
}

const commandReducer = (state=defaultState, action) => {
   switch (action.type) {
      case 'UPDATED_COMMAND':
         return action.command
      case 'RESET_COMMAND':
         return defaultState
      default:
         return state
   }
}

export default commandReducer
