export const save = (user, hist=[], inventory=[], knownObjects=['crowbar','door','desk','drawer','paper','candle','chest'], brokenObjects=[]) => {
   return dispatch => {
      fetch(`http://localhost:3000/api/v1/users/${user.id}`, {
         method: 'POST',
         headers: {
            "Content-Type": "application/json",
         },
         credentials: 'include',
         body: JSON.stringify({
            history: hist,
            inventory: inventory,
            known_objects: knownObjects,
            broken_objects: brokenObjects
         })
      })
      .then(res => res.json())
      .then(data => {
         console.log(data)
         dispatch({
            type: "USER_HISTORY_FETCH_SUCCESS",
            payload: {
               userHistory: hist,
               userObjects: inventory,
               knownObjects,
               brokenObjects
            }
         })
      })
   }
}

export const retrieve = (user) => {
   return dispatch => {
      fetch(`http://localhost:3000/api/v1/users/${user.id}`, {
         method: 'GET',
         headers: {
            "Content-Type": "application/json",
         },
         credentials: 'include'
      })
      .then(res => res.json())
      .then(data => {
         dispatch({
            type: "USER_HISTORY_FETCH_SUCCESS",
            payload: {
               userHistory: data.history,
               userObjects: data.inventory,
               knownObjects: data.known_objects,
               brokenObjects: data.broken_objects
            }
         })
      })
   }
}
